mapper.select:

select 

from wechat_messageresponse
where  USERID = #{userid,jdbcType=VARCHAR}
  and KEYWORD = #{keyword,jdbcType=VARCHAR}
  and REQUESTTYPE = #{requesttype,jdbcType=VARCHAR}

mapper.delete:

delete from wechat_messageresponse
where USERID = #{userid,jdbcType=VARCHAR}
  and KEYWORD = #{keyword,jdbcType=VARCHAR}
  and REQUESTTYPE = #{requesttype,jdbcType=VARCHAR}

mapper.update:

update wechat_messageresponse


    RESPONSETYPE = #{responsetype,jdbcType=VARCHAR},


    TITLE = #{title,jdbcType=VARCHAR},


    CONTENT = #{content,jdbcType=VARCHAR},


    PICTURE = #{picture,jdbcType=VARCHAR},


    LINKURL = #{linkurl,jdbcType=VARCHAR},


where USERID = #{userid,jdbcType=VARCHAR}
  and KEYWORD = #{keyword,jdbcType=VARCHAR}
  and REQUESTTYPE = #{requesttype,jdbcType=VARCHAR}


update wechat_messageresponse
set RESPONSETYPE = #{responsetype,jdbcType=VARCHAR},
  TITLE = #{title,jdbcType=VARCHAR},
  CONTENT = #{content,jdbcType=VARCHAR},
  PICTURE = #{picture,jdbcType=VARCHAR},
  LINKURL = #{linkurl,jdbcType=VARCHAR}
where USERID = #{userid,jdbcType=VARCHAR}
  and KEYWORD = #{keyword,jdbcType=VARCHAR}
  and REQUESTTYPE = #{requesttype,jdbcType=VARCHAR}
